						BLOB_KEY, _, _, _, _, _, _, _, _, _, _ = getTDSMEDIABlobKey(w, r, docID)	

						appText := getBlobTextNoComms(w, r, BLOB_KEY)
						//fmt.Fprintf(w, "%v", appText)
						buf2.WriteString(fmt.Sprintf("%v\n", appText))						
