		//get from media
		nMediaID := 0
		//query TDSCNFG			
		cfgName := fmt.Sprintf("PERSONAL_Local_Notes_Media_ID_%v", uid)
		_, nMediaID = getTDSCNFG(w,r,1,cfgName)
