//common error messages for internet of things 
func thingsError(w http.ResponseWriter, r *http.Request, msg string) {
	w.WriteHeader(200)
	w.Write([]byte(msg))
	return	
}

//common error messages for internet of things  
func thingsError2(w http.ResponseWriter, r *http.Request, msg, msg2 string) {
	w.WriteHeader(200)
	w.Write([]byte(msg))
	w.Write([]byte(msg2))
	return	
}
