			//when client successfully detects lat/lon
			latstring := r.FormValue("lat")
			lonstring := r.FormValue("lon")
			lat, err := strconv.ParseFloat(latstring, 64)
			if err != nil {
				lat = 0.0
			}
			lon, err := strconv.ParseFloat(lonstring, 64)
			if err != nil {
				lon = 0.0
			}
